node ~/.claude/scripts/pw.mjs eval https://note.com/bluemo/n/n16b3502dfad6 --wait 5000 "
const body = document.querySelector('.note-common-styles__textnote-body');
const nodes = body.childNodes;
let result = [];
for (let i = 0; i < nodes.length; i++) {
  const n = nodes[i];
  if (n.querySelector && n.querySelector('img')) {
    const img = n.querySelector('img');
    // Get preceding text
    let prevText = '';
    for (let j = i - 1; j >= Math.max(0, i - 3); j--) {
      if (nodes[j].textContent && nodes[j].textContent.trim()) {
        prevText = nodes[j].textContent.trim().substring(0, 80);
        break;
      }
    }
    // Get following text (caption)
    let nextText = '';
    for (let j = i + 1; j <= Math.min(nodes.length - 1, i + 3); j++) {
      if (nodes[j].textContent && nodes[j].textContent.trim()) {
        nextText = nodes[j].textContent.trim().substring(0, 80);
        break;
      }
    }
    // Get figcaption
    const caption = n.querySelector('figcaption')?.textContent?.trim() || '';
    result.push({src: img.src.split('?')[0], caption, prevText, nextText});
  }
}
JSON.stringify(result, null, 2);
" 2>&1

